import React, { Component } from "react";

class MoveBuilder extends Component {
  state = {
    //needs an id for later
    moves: [
      { name: " Select one" },
      {
        name: "Waterfall",
        base_power: 12,
        duration: 2,
        energy_delta: 7,
        move_cooldown: 1.0
      },
      {
        name: "Counter",
        base_power: 8,
        duration: 1,
        energy_delta: 7,
        move_cooldown: 1.0
      },
      {
        name: "Zen Headbutt",
        base_power: 8,
        duration: 2,
        energy_delta: 6,
        move_cooldown: 1.5
      }
    ],
    selectedValue: ""
  };

  handleChange = e => {
    this.setState({ selectedValue: e.target.value });
  };
  render() {
    return (
      <div>
        <p>Select a move</p>
        <select
          value={this.state.selectedValue}
          type="text"
          onChange={this.handleChange}
        >
          {this.state.moves.map(move => {
            return (
              <option value={move.name} key={move.name}>
                {move.name}
              </option>
            );
          })}
        </select>

        <div>
          <p>{this.state.selectedValue}</p>
        </div>
      </div>
    );
  }
}

export default MoveBuilder;
