import React from "react";

const input = props => {
  switch (props.inputType) {
    case "select":
      let input = (
        <select
          value={this.state.selectedValue}
          type="text"
          onChange={props.changed}
        >
          {this.props.moves.map(move => {
            return (
              <option value={move.name} key={move.name}>
                {move.name}
              </option>
            );
          })}
        </select>
      );
      break;
  }

  return <div>{input}</div>;
};

export default input;
