import React from "react";

const input = props => {
  let input = null;
  let element = [];
  for (let key in props.moves) {
    element.push({ id: key, data: props.moves[key], name: key });
  }
  switch (props.inputType) {
    case "select":
      input = (
        <select
          value={props.selectedValue}
          type="text"
          onChange={props.changed}
        >
          {element.map(move => {
            return (
              <option value={move.name} key={move.name}>
                {move.name}
              </option>
            );
          })}
        </select>
      );
      break;
  }

  return <div>{input}</div>;
};

export default input;
