import React, { Component } from "react";

class MoveBuilder extends Component {
  state = {
    //needs an id for later
    moves: [
      {
        name: "Waterfall",
        base_power: 12,
        duration: 2,
        energy_delta: 7,
        move_cooldown: 1.0
      },
      {
        name: "Counter",
        base_power: 8,
        duration: 1,
        energy_delta: 7,
        move_cooldown: 1.0
      },
      {
        name: "Zen Headbutt",
        base_power: 8,
        duration: 2,
        energy_delta: 6,
        move_cooldown: 1.5
      }
    ]
  };

  render() {
    return (
      <div>
        <p>Select a move</p>


        <select placeholder="select one" type="text"  >
        {this.state.moves.map( (move) =>{
            return <Option/>
        })}
         </select>
      </div>
    );
  }
}

export default MoveBuilder;
