import React, { Component } from "react";
import MoveData from "../../UI/MoveData/MoveData";
import Input from "../../UI/Input/Input";

class MoveBuilder extends Component {
  state = {
    //needs an id for later
    moves: {
      Waterfall: {
        base_power: 12,
        duration: 2,
        energy_delta: 7,
        move_cooldown: 1.0
      },

      Counter: {
        base_power: 8,
        duration: 1,
        energy_delta: 7,
        move_cooldown: 1.0
      },

      Zen_Headbutt: {
        base_power: 8,
        duration: 2,
        energy_delta: 6,
        move_cooldown: 1.5
      }
    },
    selectedValue: "",
    displayedData: null
  };

  handleChange = (e, selectedValue) => {
    let data = this.state.moves[selectedValue];
    console.log(data);
    this.setState({ selectedValue: e.target.value, displayedData: data });
  };
  render() {
    // let moveData = null;
    // let savedEntry = null;
    // for (let keys in this.state.moves) {
    //   console.log(keys);
    // }
    let element = [];
    for (let key in this.state.moves) {
      element.push({ id: key, data: this.state.moves[key], name: key });
    }
    // if (this.state.selectedValue) {
    //   moveData = <MoveData name={"waterfall"} duration={2} />;
    // }
    return (
      <div>
        <p>Select a move</p>

        <Input
          inputType="select"
          selectedValue={this.state.selectedValue}
          moves={element}
          changed={event => this.handleChange(event, this.state.selectedValue)}
        />

        {this.state.selectedValue}
        {this.state.displayedData}
      </div>
    );
  }
}

export default MoveBuilder;
