import React, { Component } from "react";
import MoveData from "../../UI/MoveData/MoveData";

class MoveBuilder extends Component {
  state = {
    //needs an id for later
    moves: [
      {
        name: "Waterfall",
        base_power: 12,
        duration: 2,
        energy_delta: 7,
        move_cooldown: 1.0
      },
      {
        name: "Counter",
        base_power: 8,
        duration: 1,
        energy_delta: 7,
        move_cooldown: 1.0
      },
      {
        name: "Zen Headbutt",
        base_power: 8,
        duration: 2,
        energy_delta: 6,
        move_cooldown: 1.5
      }
    ],
    selectedValue: ""
  };

  handleChange = e => {
    this.setState({ selectedValue: e.target.value });
  };
  render() {
    let moveData = null;
    let savedEntry = null;
    for (let keys in this.state.moves) {
      console.log(keys);
    }
    if (this.state.selectedValue) {
      moveData = <MoveData name={"waterfall"} duration={2} />;
    }
    return (
      <div>
        <p>Select a move</p>

      </div>
    );
  }
}

export default MoveBuilder;
