import React, { Component } from "react";
import MoveData from "../../UI/MoveData/MoveData";
import Input from "../../UI/Input/Input";

class MoveBuilder extends Component {
  state = {
    //needs an id for later
    moves: {
      Waterfall: {
        base_power: 12,
        duration: 2,
        energy_delta: 7,
        move_cooldown: 1.0
      },

      Counter: {
        base_power: 8,
        duration: 1,
        energy_delta: 7,
        move_cooldown: 1.0
      },

      Zen_Headbutt: {
        base_power: 8,
        duration: 2,
        energy_delta: 6,
        move_cooldown: 1.5
      }
    },
    selectedValue: ""
  };

  handleChange = e => {
    this.setState({ selectedValue: e.target.value });
  };
  render() {
    let moveElementArray = null;

    for (let key in this.state.moves) {
      moveElementArray.push({
        move_name: key,
        move_data: this.state.moves[key]
      });
    }

    let moveData = null;
    let savedEntry = null;
    for (let keys in this.state.moves) {
      console.log(keys);
    }
    // if (this.state.selectedValue) {
    //   moveData = <MoveData name={"waterfall"} duration={2} />;
    // }

    let input = moveElementArray.map(element => {
      <Input
        inputType="select"
        selectedValue={this.state.selectedValue}
        moves={this.state.moves}
        changed={this.handleChange}
      />;
    });
    return (
      <div>
        <p>Select a move</p>
      </div>
    );
  }
}

export default MoveBuilder;
