import React, { Component } from "react";
import MoveData from "../../UI/MoveData/MoveData";
import Input from "../../UI/Input/Input";

class MoveBuilder extends Component {
  state = {
    //needs an id for later
    moves: {
      Waterfall: {
        base_power: 12,
        duration: 2,
        energy_delta: 7,
        move_cooldown: 1.0
      },

      Counter: {
        base_power: 8,
        duration: 1,
        energy_delta: 7,
        move_cooldown: 1.0
      },

      Zen_Headbutt: {
        base_power: 8,
        duration: 2,
        energy_delta: 6,
        move_cooldown: 1.5
      }
    },
    selectedValue: "",
    displayedData: null
  };

  handleChange = (event) => {
    this.setState({ selectedValue: e.target.value });
  };
  render() {
    let moveData = null;
    const element = [];
    for (let key in this.state.moves) {
      element.push({ id: key, data: this.state.moves[key], name: key });
    }
    if (this.state.selectedValue) {
      const data = this.state.moves[this.state.selectedValue];
      console.log("data is", data);
      moveData = (
        <MoveData name={this.state.selectedValue} duration={data.duration} />
      );
    }
    return (
      <div>
        <p>Select a move</p>
        <Input
          inputType="select"
          selectedValue={this.state.selectedValue}
          moves={element}
          changed={this.handleChange}
        />
        {this.state.selectedValue}
        {moveData}
      </div>
    );
  }
}

export default MoveBuilder;
