import React, { Component } from "react";
import MoveData from "../../UI/MoveData/MoveData";
import Input from "../../UI/Input/Input";
import classes from "./MoveBuilder.module.css";
import Header from "../../UI/Header/Header";
import Table from "../../UI/Table/Table";

class MoveBuilder extends Component {
  state = {
    //needs an id for later
    // moves: {
    //   Waterfall: {
    //     base_power: 12,
    //     duration: 2,
    //     energy_delta: 7,
    //     move_cooldown: 1.0
    //   },

    //   Counter: {
    //     base_power: 8,
    //     duration: 1,
    //     energy_delta: 7,
    //     move_cooldown: 1.0
    //   },

    //   Zen_Headbutt: {
    //     base_power: 8,
    //     duration: 2,
    //     energy_delta: 6,
    //     move_cooldown: 1.5
    //   }
    // },
    Pokemon: {
      Raichu: {
        thundershock: {
          stats: [
            {
              base_power: 3,
              duration: 1.0,
              energy_delta: 9,
              move_cooldown: 1.0
            }
          ]
        },
        spark: {
          stats: [
            {
              base_power: 3,
              duration: 1.0,
              energy_delta: 9,
              move_cooldown: 1.0
            }
          ]
        },
        voltswitch: {
          stats: [
            {
              base_power: 3,
              duration: 1.0,
              energy_delta: 9,
              move_cooldown: 1.0
            }
          ]
        }
      },
      Pikachu: {
        Thundershock: {
          stats: [
            {
              base_power: 3,
              duration: 1.0,
              energy_delta: 9,
              move_cooldown: 1.0
            }
          ]
        },
        Spark: {
          stats: [
            {
              base_power: 4,
              duration: 1.0,
              energy_delta: 8,
              move_cooldown: 1.0
            }
          ]
        },
        "Volt Switch": {
          stats: [
            {
              base_power: 12,
              duration: 4.0,
              energy_delta: 10,
              move_cooldown: 2.5
            }
          ]
        }
      },
      Pichu: {},
      Charmander: {},
      Charizard: {}
    },

    selectedValue: ""
    // displayedData: null
  };

  handleChange = (event, selectedOption) => {
    console.log(selectedOption);
    this.setState({ selectedValue: selectedOption });
  };
  render() {
    let moveData = null;
    let table = null;
    const element = [];
    for (let key in this.state.Pokemon) {
      element.push({ id: key, data: this.state.Pokemon[key], name: key });
    }

    if (this.state.selectedValue) {
      console.log("printing the value now,", this.state.selectedValue);
      const data = this.state.Pokemon[this.state.selectedValue];
      console.log("data is", data);
      //create a table comoponent to pass the data
      //create a base table gathering the data and creating the table in the component
      moveData = (
        <MoveData
          name={this.state.selectedValue}
          duration={data.duration}
          basePower={data.base_power}
          energyDelta={data.energy_delta}
          moveCooldown={data.move_cooldown}
        />
      );
      table = <Table moves={data} />;
    }
    let form = (
      <Input
        inputType="select"
        selectedValue={this.state.selectedValue}
        moves={element}
        changed={this.handleChange}
      />
    );

    return (
      <div>
        <Header />
        <div className={classes.MoveBuilder}>{form}</div>
        {moveData}
        {table}
      </div>
    );
  }
}

export default MoveBuilder;
