import React from "react";
import classes from "../MoveData/"
const input = props => {
  let input = null;

  console.log("MOVES ARE", props.moves);
  switch (props.inputType) {
    case "select":
      input = (
        <select
          value={props.selectedValue}
          type="text"
          onChange={props.changed}
        >
          {props.moves.map(move => {
            return (
              <option value={move.name} key={move.id}>
                {move.name}
              </option>
            );
          })}
        </select>
      );
      break;
  }

  return <div className={classes.Input}>{input}</div>;
};

export default input;
