import React, { Component } from "react";
import classes from "./FormPage.module.css";
import Input from "../UI/Input/Input";
{
  /* <label for="exampleInputEmail1">Pokemon name</label>
        <input
          type="text"
          class="form-control"
          aria-describedby="emailHelp"
          placeholder="Enter Pokemon Name"
        />
  */
}
// handleExtraQuickMove = () => {};
class FormPage extends Component {
  state = {
    newPokemon: {
      pokemonName: null
    },
    newQuickMove: {
      moveName: null
    },
    // quickMoveName: {

    // }

    newChargeMove: {
      name: {
        value: "",
        label: "Charge Move Name",
        inputType: "input",
        elementConfig: {
          type: "text",
          placeholder: "Enter Charge Move Name"
        }
      },
      basePower: {
        value: "",
        inputType: "input",
        label: "Base Power",
        elementConfig: {
          type: "number",
          placeholder: "0"
        }
      },
      chargeEnergy: {
        value: "",
        label: "Charge Energy",
        inputType: "input",
        elementConfig: {
          type: "number",
          placeholder: "0"
        }
      },
      damagePerEnergy: {
        value: "",
        label: "Damage Per Energy",
        inputType: "input",
        elementConfig: {
          type: "number",
          placeholder: "0"
        }
      }
    }
  };

  //submit charge moves only for now
  //figure out the id of which input was seelcted
  //like the contact data example

  handleChange = (event, key) => {
    const copyState = { ...this.state.newChargeMove };
    const updatedElementState = { ...copyState[key] };
    updatedElementState.value = event.target.value;
    copyState[key] = updatedElementState;

    this.setState({ newChargeMove: copyState });
  };
  handleSubmit = event => {
    event.preventDefault();
    const { newChargeMove } = this.state;
    fetch(
      `http://localhost:3036/chargemoves/add?chargeMoveName=${
        newChargeMove.name.value
      }&basePower=${newChargeMove.basePower.value}&energy=${
        newChargeMove.chargeEnergy.value
      }&damagePerEnergy=${newChargeMove.damagePerEnergy.value}`
    ).then(response => this.props.history.push("/")).catch(error=>{

    });
  };
  render() {
    console.log("the option is ", this.props.selectedOption);
    const array = [];

    for (let key in this.state.newChargeMove) {
      array.push({ id: key, config: this.state.newChargeMove[key] });
    }
    console.log("the array is ", array);
    let form = null;
    console.log("inside the switch");
    switch (this.props.selectedOption) {
      case "Pokemon":
        form = (
          <form className={classes.FormPage}>
            <div className="form-group">
              <Input
                inputType="pokemon"
                label="Enter Pokemon Name"
                placeholder="Enter Pokemon Name"
                className="form-control"
                change={event => this.handleChange(event)}
              />
            </div>

            <div className="form-group" style={{ border: "white solid 1px" }}>
              <Input
                inputType="pokemon"
                label="Enter Quick Move Name"
                placeholder="Enter Quick Move"
                className="form-control"
              />
              <Input inputType="quickMoveData" className="form-control" />
            </div>
            <div className="form-group" style={{ border: "white solid 1px" }}>
              <Input
                inputType="pokemon"
                label="Enter Quick Move Name"
                placeholder="Enter Quick Move"
                className="form-control"
              />
              <Input inputType="quickMoveData" className="form-control" />
            </div>

            {/* <button onClick={this.handleExtraQuickMove}>Add a quick move</button> */}
            <div className="form-group" style={{ border: "white solid 1px" }}>
              <Input
                inputType="pokemon"
                label="Enter Charge Move Name"
                placeholder="Enter charge move name"
                className="form-control"
              />
              <Input inputType="chargeMoveData" className="form-control" />
            </div>
            <div className="form-group" style={{ border: "white solid 1px" }}>
              <Input
                inputType="pokemon"
                label="Enter Charge Move Name"
                placeholder="Enter charge move name"
                className="form-control"
              />
              <Input inputType="chargeMoveData" className="form-control" />
            </div>
            <div className="form-group" style={{ border: "white solid 1px" }}>
              <Input
                inputType="pokemon"
                label="Enter Charge Move Name"
                placeholder="Enter charge move name"
                className="form-control"
                changed={event => this.handleChange(event)}
              />
              <Input inputType="chargeMoveData" className="form-control" />
            </div>
          </form>
        );
        break;
      case "Quick Move":
        form = (
          <form className={classes.FormPage}>
            <div className="form-group" style={{ border: "white solid 1px" }}>
              <Input
                inputType="pokemon"
                label="Enter Quick Move Name"
                placeholder="Enter Quick Move"
                className="form-control"
              />
              <Input inputType="quickMoveData" className="form-control" />
            </div>
          </form>
        );
        break;
      case "Charge Move":
        form = (
          <form className={classes.FormPage}>
            {array.map(element => (
              <Input
                key={element.id}
                inputType={element.config.inputType}
                label={element.config.label}
                value={element.config.value}
                elementConfig={element.config.elementConfig}
                changed={event => this.handleChange(event, element.id)}
              />
            ))}
            <button type="submit" className="btn btn-primary">
              Submit
            </button>
          </form>
        );

        // form = (
        //   <form className={classes.FormPage}>
        //     <div className="form-group" style={{ border: "white solid 1px" }}>
        //       <Input
        //         inputType="pokemon"
        //         label="Enter Charge Move Name"
        //         placeholder="Enter charge move name"
        //         className="form-control"
        //       />
        //       <Input
        //         inputType="chargeMoveData"
        //         className="form-control"
        //         change={this.handleChange}
        //       />
        //     </div>
        //   </form>
        // );
        break;

      default:
        form = <div>hello world</div>;
    }
    return <div className={classes.Input}>{form}</div>;
  }

  // {
  //   /* <div className="container" style={{ border: "2px solid red" }}>
  //       <div className="row">
  //         <div class="col-sm">
  //           Base Power <input placeholder="0" type="number" step="0.5" />
  //         </div>
  //         <div class="col-sm">
  //           Turn <input placeholder="0" type="number" step="0.5" />
  //         </div>
  //         <div class="col-sm">
  //           Energy Delta
  //           <input placeholder="0" type="number" step="0.5" />
  //         </div>
  //       </div>
  //       <br />
  //       <div className="row">
  //         <div class="col-md">
  //           Energy Per Turn <input placeholder="0" type="number" step="0.5" />
  //         </div>
  //         <div class="col-md">
  //           Damage Per Duration{" "}
  //           <input placeholder="0" type="number" step="0.5" />
  //         </div>
  //       </div> */
  // }
  // {
  //   /* <label>Turn</label>
  //         <input placeholder="text" />

  //         <label>Energy Delta </label>
  //         <input placeholder="Turn" />
  //       </div> */
  // }
  // {
  //   /* <div className="row">
  //         <label>Energy Per Turn </label>
  //         <input placeholder="Turn" />
  //         <label>Energy Per Turn </label>
  //         <input placeholder="Turn" />
  //         <label>Energy Per Turn </label>
  //         <input placeholder="Turn" />
  //       </div> */
  // }
  // {
  //   /* </div> */
  // }
}

export default FormPage;
